import React, { useState, useCallback, useRef } from 'react';
import ReactFlow, {
  Controls,
  Background,
  useNodesState,
  useEdgesState,
  addEdge,
} from 'reactflow';
import 'reactflow/dist/style.css';
import { toPng } from 'html-to-image';

// Custom node component
const CustomNode = ({ data }) => {
  return (
    <div style={{ padding: '10px', border: '1px solid #ddd', borderRadius: '5px', background: 'white' }}>
      <div>{data.label}</div>
      {data.note && <div style={{ fontSize: '10px', color: '#888' }}>{data.note}</div>}
    </div>
  );
};

const nodeTypes = {
  custom: CustomNode,
};

function MindMap({ data, onUpdate }) {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  // const [translate, setTranslate] = useState({ x: 300, y: 50 });
  const [editingNode, setEditingNode] = useState(null);
  const reactFlowWrapper = useRef(null);
  const reactFlowInstance = useRef(null);

  // Convert mind map data to React Flow format
  React.useEffect(() => {
    if (data) {
      const { nodes: newNodes, edges: newEdges } = convertToReactFlowFormat(data);
      setNodes(newNodes);
      setEdges(newEdges);
    }
  }, [data, setNodes, setEdges]);

  const onConnect = useCallback((params) => setEdges((eds) => addEdge(params, eds)), [setEdges]);
  
  const handleNodeClick = (event, node) => {
    // Handle node click for editing
    setEditingNode(node);
  };

  const handleEditSubmit = (e) => {
    e.preventDefault();
    const updatedNode = {
      ...editingNode,
      data: {
        ...editingNode.data,
        label: e.target.elements.nodeName.value,
        note: e.target.elements.nodeNote.value,
      },
    };
    setNodes((nds) => nds.map((node) => (node.id === updatedNode.id ? updatedNode : node)));
    onUpdate({ updatedNode });
    setEditingNode(null);
  };

  const handleSave = useCallback(() => {
    if (reactFlowInstance.current && reactFlowWrapper.current) {
      const nodesBounds = reactFlowInstance.current.getNodes().reduce(
        (bounds, node) => {
          bounds.minX = Math.min(bounds.minX, node.position.x);
          bounds.minY = Math.min(bounds.minY, node.position.y);
          bounds.maxX = Math.max(bounds.maxX, node.position.x + node.width);
          bounds.maxY = Math.max(bounds.maxY, node.position.y + node.height);
          return bounds;
        },
        { minX: Infinity, minY: Infinity, maxX: -Infinity, maxY: -Infinity }
      );

      const width = nodesBounds.maxX - nodesBounds.minX + 50;
      const height = nodesBounds.maxY - nodesBounds.minY + 50;
      const transformX = -nodesBounds.minX + 25;
      const transformY = -nodesBounds.minY + 25;

      reactFlowInstance.current.setViewport({ x: transformX, y: transformY, zoom: 1 });

    
      toPng(reactFlowWrapper.current, { 
        cacheBust: true, 
        backgroundColor: '#ffffff',
        width: width,
        height: height,
        style: {
          width: `${width}px`,
          height: `${height}px`,
        },
      })
        .then((dataUrl) => {
          const link = document.createElement('a');
          link.download = 'mind-map.png';
          link.href = dataUrl;
          link.click();
        })
        .catch((error) => {
          console.error('Error saving mind map:', error);
        });
    }
  }, []);

  const handleDisplay = () => {
    if (reactFlowWrapper.current === null) {
      return;
    }

    // const node = document.getElementById('mind-map-container');
    toPng(reactFlowWrapper.current, { cacheBust: true, })
      .then((dataUrl) => {
        const img = new Image();
        img.src = dataUrl;
        document.body.appendChild(img);
      })
      .catch((error) => {
        console.error('Error displaying mind map:', error);
      });
  }

  console.log('MindMap component received data:', data)
  return (
    <div ref={reactFlowWrapper} style={{ width: '100%', height: '500px', border: '1px solid black' }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        onNodeClick={handleNodeClick}
        nodeTypes={nodeTypes}
        onInit={(instance) => (reactFlowInstance.current = instance)}
        fitView
      >
        <Controls />
        <Background variant="dots" gap={12} size={1} />
      </ReactFlow>
      {editingNode && (
        <div style={{ position: 'absolute', top:10, right:10, background: 'white', padding:10, zIndex: 1000}}>
          <form onSubmit={handleEditSubmit}>
            <input name="nodeName" defaultValue={editingNode.data.label} />
            <textarea name="nodeNote" defaultValue={editingNode.data.note || ''} />
            <button type="submit">Update</button>
          </form> 
        </div>
      )}
      <button onClick={handleSave} style={{ position: 'absolute', bottom: 10, right: 10 }} >Save as PNG</button>
    </div>

  );
}

// Helper function to convert mind map data to React Flow format
function convertToReactFlowFormat(data, parentId = null, x = 0, y = 0) {
  const nodes = [];
  const edges = [];

  function processNode(node, parentId, x, y) {
    const id = node.name.replace(/\s+/g, '-').toLowerCase();
    nodes.push({
      id,
      type: 'custom',
      position: { x, y },
      data: { label: node.name, note: node.attributes?.note },
    });

  if (parentId) {
    edges.push({ id: `${parentId}-${id}`, source: parentId, target: id });
  }

  if (node.children) {
    const childSpacing = 150;
    node.children.forEach((child, index) => {
      const childX = x + 200;
      const childY = y + (index - (node.children.length - 1) / 2) * childSpacing;
      const { childNodes, childEdges } = processNode(child, id, childX, childY);
      nodes.push(...childNodes);
      edges.push(...childEdges);
    });
  }

  return { childNodes: nodes, childEdges: edges };
}

const { childNodes, childEdges } = processNode(data, parentId, x, y);
return { nodes: childNodes, edges: childEdges };
}
export default MindMap;
